sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

    // Get the number of additional overdue NeedIt tasks for the Assigned To.

	// Construct a GlideRecord query to find all NeedIt Task records with Due date
	// field values in the past for the same Assigned to.  The records cannot be in the
	// Closed state.
	var now = new GlideDateTime();

// 	var gr = new GlideRecord("x_58872_needit_needit_task");
// 	gr.addQuery("due_date", "<", now);
// 	gr.addQuery("assigned_to", current.assigned_to);
// 	gr.addQuery("state", "!=", 3);
// 	gr.query();

// 	// Print the count to the message field.
// 	template.print("You have a total of " + gr.getRowCount() + " overdue tasks!");

	var gragg = new GlideAggregate("x_58872_needit_needit_task");
	gragg.addQuery("due_date", "<", now);
	gragg.addQuery("assigned_to", current.assigned_to);
	gragg.addQuery("state", "!=", 3);
	gragg.addAggregate("COUNT");//, "sys_id");
	gragg.query();

	if (gragg.next()) {
		var count = gragg.getAggregate("COUNT");//, "sys_id");
		if (parseInt(count) > 0) {
			// Print the count to the message field.
			template.print("You have a total of " + count + " overdue tasks!");			
		}
	}
})(current, template, email, email_action, event);
