sysauto_script.script:
// Get today's date and time.
var now = new GlideDateTime();
var nowOneDayFwd = gs.hoursAgo(-24);

//gs.info("LAST TIME: now={0}; 24hrs={1}", now.toString(), nowOneDayFwd);

// Query the database for NeedIt Task records with Due date field values between
// the current time and 24 hours from now.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var grSoon = new GlideRecord("x_58872_needit_needit_task");
grSoon.addQuery("due_date", ">=", now);
grSoon.addQuery("due_date", "<", nowOneDayFwd);
grSoon.addQuery("state", "!=", 3); // Closed Complete
grSoon.query();

// Write a log message for each overdue NeedIt Task Record
while(grSoon.next()) {
	gs.info("{0} (Task # = {1}) are due within the next 24 hours", grSoon.short_description, grSoon.number);
	gs.eventQueue("x_58872_needit.dueSoonNeedItTask", grSoon, gs.getUserID(), gs.getUserName());
}
