sysauto_script.script:
// Retrieve the value of the x_58872_needit.autoCloseOverdue Application Property
var autoClose = gs.getProperty("x_58872_needit.autoCloseOverdue");
var autoCloseMin = gs.daysAgo(autoClose);

// Query the database for NeedIt Task records with Due date field
// values older the number of days set in the x_58872_needit.autoCloseOverdue
// Application Property.  Only return NeedIt Task records have a State value of
// Open or Work in progress.  Close the NeedIt Tasks and update
// the NeedIt Task Work notes.
var tasks = new GlideRecord("x_58872_needit_needit_task");
tasks.addQuery("due_date", "<=", autoCloseMin);
tasks.addQuery("state", "IN", "1,2");
tasks.query();

while(tasks.next()) {
	gs.info("Auto close NeedIt task record " + tasks.number);
	tasks.state = 4; // Closed Incomplete
	tasks.work_notes = "Closed task because the Due date was beyond overdue";
	tasks.update();
}
